    path: '/login',
    name: 'login',
    component: () => import(/* webpackChunkName: "about" */ '../views/root/LoginView.vue')
  },
  {
    path: '/',
    redirect:'/login'
  },
  {
	  path: '/main',
	  name: 'main',
	  component: () => import(/* webpackChunkName: "about" */ '../views/root/MainView.html')
